    def connect_db(self) -> bool:
        """Connect to PostgreSQL database"""
        try:
            self.db_connection = psycopg2.connect(**self.postgres_config)
            logger.info("âœ… Connected to PostgreSQL database")
            return True
        except Exception as e:
            logger.error(f"âŒ Failed to connect to database: {e}")
            return False
            
    def close_db(self):
        """Close database connection"""
        if self.db_connection:
            self.db_connection.close()
            logger.info("âœ… Database connection closed")

    def fetch_strapi_content(self, content_type: str, tenant_filter: str = None) -> List[Dict]:
        """Fetch content from Strapi API with enhanced error handling"""
        try:
            # Build API URL
            url = f"{self.strapi_url}/api/{content_type}"
            params = {'populate': '*'}
            
            if tenant_filter:
                params['filters[tenant][$eq]'] = tenant_filter
                
            response = requests.get(url, params=params, timeout=30)
            
            if response.status_code == 200:
                data = response.json()
                items = data.get('data', [])
                logger.info(f"âœ… Fetched {len(items)} {content_type} items from Strapi API")
                return items
            else:
                logger.warning(f"âš ï¸ Strapi API returned status {response.status_code} for {content_type}")
                
        except requests.exceptions.RequestException as e:
            logger.warning(f"âš ï¸ Could not fetch from Strapi API: {e}")
        except Exception as e:
            logger.error(f"âŒ Unexpected error fetching Strapi content: {e}")
        
        # Fallback to database query
        return self.fetch_from_database(content_type, tenant_filter)
    
    def fetch_from_database(self, content_type: str, tenant_filter: str = None) -> List[Dict]:
        """Fetch content directly from database with enhanced querying"""
        if not self.db_connection:
            logger.error("âŒ No database connection")
            return []
        
        # Enhanced table mapping for Strapi content types
        table_mapping = {
            'articles': 'articles',
            'blog-posts': 'blog_posts', 
            'services': 'services',
            'pages': 'pages',
            'case-studies': 'case_studies',
            'team-members': 'team_members',
            'testimonials': 'testimonials',
            'landing-pages': 'landing_pages',
            'faqs': 'faqs'
        }
        
        table_name = table_mapping.get(content_type, content_type.replace('-', '_'))
        
        try:
            cursor = self.db_connection.cursor()
            
            # Build query with tenant filtering
            base_query = f"SELECT * FROM {table_name}"
            params = []
            
            if tenant_filter:
                base_query += " WHERE tenant = %s OR tenant = 'both' OR tenant IS NULL"
                params.append(tenant_filter)
            
            cursor.execute(base_query, params)
            
            columns = [desc[0] for desc in cursor.description]
            rows = cursor.fetchall()
            
            # Convert to list of dicts
            result = []
            for row in rows:
                result.append(dict(zip(columns, row)))
            
            logger.info(f"âœ… Fetched {len(result)} {content_type} items from database")
            return result
            
        except Exception as e:
            logger.error(f"âŒ Database query failed for {content_type}: {e}")
            return []

